mapper.select:
select id, CASE classify WHEN '1' THEN '垃圾散乱' WHEN '2' THEN '绿化损坏' WHEN '3' THEN '高空抛物' WHEN '井盖缺失' THEN '4' WHEN '5' THEN '路面破损' WHEN '6' THEN '违章搭建' WHEN '7' THEN '违规停车' end as 'classify', title, content, img_url, telephone, create_user_id, create_user_name, create_time, examine_id, examine_name, modify_user_id, modify_time, case sts when '1' then '正常'else '删除' end as 'sts' , case STATUS when '0' then '待处理' when '1' then '处理中' when '2' then '已处理' when '3' then '已超时' when '4' then '已督查' end as 'status', over_time, village_code from st_applet_environment where village_code = #{villageCode}
and classify = #{type}
and status = #{status}
and sts = #{sts}
