mapper.select:
select

from st_applet_parking
where create_user_id = #{userId}
and village_code = #{villageCode}

  and sts = #{sts}

select id, car_num, price, start_time, end_time, telephone, praking_num, case sts when '0' then '待处理' when '1' then '已通过' when '2' then '已到期' when '3' then '未通过' when '4' then '删除' end as sts , create_time,create_user_id, create_user_name, modify_time, modify_user_id, modify_user_name, village_code , examine_id,examine_name,remarks from st_applet_parking where village_code = #{villageCode}
and sts = #{sts}
order by create_time desc
