mapper.select:
SELECT id, case payment_type when '1' then '支付宝' when '2' then '微信' else '银行卡' end as payment_type, payment_code, qr_code, case sts when '1' then '正常' else '停用' end as sts , create_user_id, create_user_name, create_time, modify_user_id, modify_user_name, modify_time, village_code FROM st_applet_payment where village_code = #{villageCode}
and payment_type = #{type}
and sts = #{sts}

mapper.insert:
insert into st_applet_payment (id, payment_type, payment_code, 
  qr_code, sts, create_user_id, 
  create_user_name, create_time, modify_user_id, 
  modify_user_name, modify_time, village_code
  )
values (#{id,jdbcType=BIGINT}, #{paymentType,jdbcType=VARCHAR}, #{paymentCode,jdbcType=VARCHAR}, 
  #{qrCode,jdbcType=VARCHAR}, #{sts,jdbcType=VARCHAR}, #{createUserId,jdbcType=VARCHAR}, 
  #{createUserName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{modifyUserId,jdbcType=VARCHAR}, 
  #{modifyUserName,jdbcType=VARCHAR}, #{modifyTime,jdbcType=TIMESTAMP}, #{villageCode,jdbcType=VARCHAR}
  )
