mapper.select:
select t.id, CASE t.task_type WHEN '1' THEN '拼车' WHEN '2' THEN '车位共享' WHEN '3' THEN '时间互换' ELSE '资源共享' END AS taskType, t.title, t.create_user_id as userId , t.content, t.create_time AS createTime, apply_num as applyNum, case t.share_status WHEN '0' THEN '未申请' WHEN '1' THEN '已申请' when '2' then '关闭申请' ELSE '共享完成' END as shareStatus , CASE t.sts WHEN '0' THEN '已删除' else '正常' end as sts , t.create_user_name as userName , t.contact as contact , t.telephone as telephone FROM st_applet_share_info t WHERE 1= 1 and t.share_type = '2' and village_code = #{villageCode}
and t.task_type = #{taskType}
and t.sts = #{status}
and t.create_user_id = #{userId}
ORDER BY t.create_time DESC

select t.id, CASE t.task_type WHEN '1' THEN '拼车' WHEN '2' THEN '车位共享' WHEN '3' THEN '时间互换' ELSE '资源共享' END AS taskType, t.title, t.create_user_id as userId , t.content, t.create_time AS createTime, t.img_url as imgUrl, CASE t.sts WHEN '0' THEN '已删除' else '正常' end as sts , t.create_user_name as userName , t.contact as contact , t.telephone as telephone FROM st_applet_share_info t WHERE 1= 1 and village_code = #{villageCode}
and t.task_type = #{taskType}
and t.sts = #{status}
ORDER BY t.create_time DESC

SELECT t.id, CASE t.task_type WHEN '1' THEN '拼车' WHEN '2' THEN '车位共享' WHEN '3' THEN '时间互换' ELSE '资源共享' END AS taskType, t.title, t.content, t.create_time AS createTime, t.sts, t.img_url AS imgUrl FROM st_applet_share_info t WHERE t.create_user_id = #{userId} and t.village_code = #{villageCode}
and t.task_type = #{taskType}
and t.sts = '1' ORDER BY t.create_time DESC

SELECT t.id, CASE t.task_type WHEN '1' THEN '拼车' WHEN '2' THEN '车位共享' WHEN '3' THEN '时间互换' ELSE '资源共享' END AS taskType, t.title, t.content, t.create_time AS createTime, t.sts, t.img_url AS imgUrl FROM st_applet_share_info t WHERE t.sts = '1' and share_status != '2'
and t.task_type = #{taskType}
ORDER BY t.create_time DESC
